over{background:#888}#header{text-align:center;color:#fff;margin:0}#header h1,#header p{margin:0;padding:0 5px;line-height:70px;font-size:40px;font-weight:700}#header a{color:#fff;text-decoration:none}#header a:hover{color:#333;cursor:pointer}#header .description{display:none}#header img{height:auto;width:100%;display:block;margin-left:auto;margin-right:auto}#homepage-wrapper{background:#2F3A67;width:100%;margin:-3px 0 10px;display:block;text-align:center}#homepage-wrapper .icon{padding:30px 0 0}#homepage-wrapper .icon h1{font-weight:400;font-size:2.3em;color:#fff;text-transform:uppercase;line-height:1.5em;padding:0;margin:0}#homepage-wrapper .pencarian{margin:0 auto 10px;text-align:center;color:#fff;padding:0 1em 1em}#homepage-wrapper .tuliscari{padding:3px 10px;border:0;font-size:1.3em;line-height:1.5em;width:240px;margin:0 0 10px;color:#888}#homepage-wrapper .tombol{background:#999;text-transform:uppercase;font-size:1.3em;margin:0 0 0 10px;padding:5px 21px;border:1px solid #999;color:#fff;cursor:pointer;display:inline-block}#homepage-wrapper .formcari{display:inline-block;margin:0 auto;padding:10px 0}#bottom-wrapper{border-top:1px solid #888;width:100%;margin-top:40px;display:inline-block;float:left;background:#2F3A67}.credit{padding:10px;text-align:center}.credit p{font-size:1.4em;line-height:1.5em;margin:0;color:#fff;text-decoration:none}.credit a:link,.credit a:visited{color:#fff}.credit a:hover{color:#eee}fieldset{border:none}label{float:left;text-indent:-99999px}.wrapper{width:90%;max-width:1200px;margin:0 auto;overflow-y:hidden}.display{position:relative;left:0;height:600px}.display div{background-repeat:no-repeat;background-size:100%;overflow:hidden}iframe{transform:scale(0.219);-webkit-transform:scale(0.219);-o-transform:scale(0.219);-ms-transform:scale(0.219);-moz-transform:scale(0.219);transform-origin:top left;-webkit-transform-origin:top left;-o-transform-origin:top left;-ms-transform-origin:top left;-moz-transform-origin:top left;margin:0;padding:0;position:relative;background-color:#fff;border-color:#000}.mobile{background-image:url(./img/iphone.png);position:absolute;width:95px;height:196px;top:375px;left:300px;z-index:5}.mobile iframe{width:320px;height:480px;top:32px;left:11px;overflow-y:hidden}.tablet{background-image:url(./img/ipad.png);width:246px;height:400px;z-index:3;position:absolute;left:120px;top:230px}.tablet iframe{width:768px;height:1024px;top:35px;left:38px;overflow-y:hidden}.laptop{background-image:url(./img/laptop.png);width:477px;height:307px;top:264px;left:560px;position:absolute;z-index:2}.laptop iframe{width:1280px;height:802px;top:26px;left:60px;transform:scale(0.277);-webkit-transform:scale(0.277);-o-transform:scale(0.277);-ms-transform:scale(0.277);-moz-transform:scale(0.277);transform-origin:top left;-webkit-transform-origin:top left;-o-transform-origin:top left;-ms-transform-origin:top left;-moz-transform-origin:top left}.desktop{position:absolute;width:566px;height:538px;background-image:url(./img/desktop.png);top:0;left:220px;z-index:1}.desktop iframe{left:28px;top:38px;width:1600px;height:992px;transform:scale(0.3181);-webkit-transform:scale(0.3181);-o-transform:scale(0.3181);-ms-transform:scale(0.3181);-moz-transform:scale(0.3181);transform-origin:top left;-webkit-transform-origin:top left;-o-transform-origin:top left;-ms-transform-origin:top left;-moz-transform-origin:top left}@media (max-width: 1160px){.display{width:95%;height:550px;transform:scale(0.81);-webkit-transform:scale(0.81);-o-transform:scale(0.81);-ms-transform:scale(0.81);-moz-transform:scale(0.81)}.desktop{left:180px}.laptop{left:520px}.tablet{left:80px}.mobile{left:260px}}@media (max-width: 1070px){.display{left:-50px}}@media (max-width: 1000px){.display{height:500px;transform:scale(0.71);-webkit-transform:scale(0.71);-o-transform:scale(0.71);-ms-transform:scale(0.71);-moz-transform:scale(0.71);top:-40px}.desktop{left:140px}.laptop{left:480px}.tablet{left:40px}.mobile{left:220px}}@media (max-width: 850px){.display{height:500px;transform:scale(0.65);-webkit-transform:scale(0.65);-o-transform:scale(0.65);-ms-transform:scale(0.65);-moz-transform:scale(0.65)}.desktop{left:100px}.laptop{left:440px}.tablet{left:0}.mobile{left:180px}}@media (max-width: 768px){.display{height:450px;transform:scale(0.55);-webkit-transform:scale(0.55);-o-transform:scale(0.55);-ms-transform:scale(0.55);-moz-transform:scale(0.55)}}@media (max-width: 670px){.display{height:400px;left:-70px;transform:scale(0.45);-webkit-transform:scale(0.45);-o-transform:scale(0.45);-ms-transform:scale(0.45);-moz-transform:scale(0.45)}}@media (max-width: 560px){.display{height:270px;top:-65px;transform:scale(0.37);-webkit-transform:scale(0.37);-o-transform:scale(0.37);-ms-transform:scale(0.37);-moz-transform:scale(0.37)}}@media (max-width: 440px){.display{left:-17px;top:-65px;width:70px;transform:scale(0.35);-webkit-transform:scale(0.35);-o-transform:scale(0.35);-ms-transform:scale(0.35);-moz-transform:scale(0.35)}}@media (max-width: 380px){.display{height:235px;left:-17px;top:-65px;width:70px;transform:scale(0.27);-webkit-transform:scale(0.27);-o-transform:scale(0.27);-ms-transform:scale(0.27);-moz-transform:scale(0.27)}.desktop{left:100px}.laptop{left:515px}.tablet{left:0}.mobile{left:180px}}
