			function getUrlVars() {var vars = {};var parts = window.location.href.replace(/[?&]+([^=&]+)=([^&]*)/gi, function(m,key,value) {vars[key] = value;});return vars;}var first = getUrlVars()["url"];var first = decodeURIComponent(first);var first = first.replace(/\#$/, '');if(first === "undefined") {}else {$("iframe").attr('src',(first));$('#url').attr('value',(first));}
